    toggle() {
        if (this.isMobile()) {
            this.isOpen.update(v => !v);
        } else {
            this.isCollapsed.update(v => !v);
        }
    }

    open() {
        this.isOpen.set(true);
    }

    close() {
        this.isOpen.set(false);
    }

    setMobile(isMobile: boolean) {
        this.isMobile.set(isMobile);
