@Component({
    selector: 'ui-table-footer',
    changeDetection: ChangeDetectionStrategy.OnPush,
    template: `<tfoot [class]="classes()" [attr.data-slot]="'table-footer'"><ng-content /></tfoot>`,
    host: { class: 'contents' },
})
export class TableFooterComponent {
