    // Calendar emits DateRange for range mode
    if (value && typeof value === 'object' && 'start' in value) {
      const range = value as DateRange;
      this.rangeValue.set(range);
      this.rangeChange.emit(range);
      this.onChange(range);
      this.onTouched();

