    this.menus.set(id, { trigger });
  }

  unregister(id: string) {
    this.menus.delete(id);
  }

  setActive(id: string | null) {
    this.activeMenuId.set(id);
  }

  isActive(id: string) {
    return this.activeMenuId() === id;
  }
}

