    const index = triggers.indexOf(this.triggerEl.nativeElement);
    const prevIndex = (index - 1 + triggers.length) % triggers.length;
    triggers[prevIndex]?.focus();
    if (this.service.activeMenuId()) {
      triggers[prevIndex].click();
    }
  }
